DROP TABLE MANCHESTER CASCADE CONSTRAINTS;
DROP TABLE PLAYERS CASCADE CONSTRAINTS;

drop sequence manchester_idno_seq;
create sequence manchester_idno_seq start with 444 increment by 111;



CREATE TABLE MANCHESTER (
	idno number(5) NOT NULL,
	team varchar2(10 char) NOT NULL,
	PRIMARY KEY (idno)
);

CREATE TABLE PLAYERS (
	idno number(5) NOT NULL,
	memberno number(4) NOT NULL,
	backnumber number(2),
	position varchar2(20 char) NOT NULL,
	name varchar2(25 char) NOT NULL,
	nationality varchar2(15 char) NOT NULL,
	dateofjoin date NOT NULL,
	PRIMARY KEY (memberno)
);



